  /**
  * @brief 記号列の受理を判定する
  * @param ch 入力文字
  * @param row_index 参照するテーブル番号
  */
  [[nodiscard]]
  constexpr auto ref_symbol_table(char8_t ch, int row_index = 0) -> int {
    //制御文字は考慮しない
    std::uint8_t index = ch - 33;

    //Ascii範囲外の文字か制御文字
    if ((126 - 33) < index) return -1;

    //テーブルを参照して受理すべきかを決定
    return kusabira::table::op_punc_table[row_index][index];
  }
